import React from 'react';
import  './App.css';

class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      author: '',
      quote: '',
    }
    this.handleClick = this.handleClick.bind(this);
    this.chooseQuote = this.chooseQuote.bind(this);
  }

  componentDidMount(){
    fetch('https://gist.githubusercontent.com/apatrickg/de7a7fae9ab4bd94d7965d01ca253f21/raw/84a56b56c8de22a2a571e80a897189a19ea1b6df/quotes.json')
      .then(response=> response.json())
  }

  render (){
    return (
      <div id="quote-box">
        <div id="text"></div>
        <div id="author"></div>
        <div id="new-quote"></div>
      </div>
    )
  }
}

export default App;
