import React from 'react';
import  './App.css';

class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      quoteList:[],
      author: '',
      quote: '',
    }
  }

  componentDidMount(){
    fetch('http://my-json-server.typicode.com/apatrickg/quotes/db')
      .then(response=> response.json())
      .then(quotes => this.setState({ quoteList:quotes }))
  }

  chooseQuote() {
    let randomIndex = Math.floor(Math.random() * this.state.quoteList.length)
    return this.state.quoteList[randomIndex]
  }

  handleClick() {
    const randomQuote = this.chooseQuote();
    this.setState({
      quote: randomQuote.quote,
      author:randomQuote.author
    })
  }

  render (){
    return (
      <div id="quote-box">
        <div id="text"><h1>{this.state.quote}</h1></div>
        <div id="author"></div>
        <div id="new-quote"></div>
      </div>
    )
  }
}

export default App;
