import React from 'react';
import  './App.css';

class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      quotesDB:[],
      quote: '',
      author: ''
    }
    this.retrieveQuote = this.retrieveQuote.bind(this)
  }

  componentDidMount(){
    fetch('https://gist.githubusercontent.com/apatrickg/de7a7fae9ab4bd94d7965d01ca253f21/raw/5da320eaa183f3047bbd55cf3d1c36a3847ebd66/quotes.json')
    .then(response => response.json())
    .then(data => {
      this.setState({
        quotesDB: data.quotes
      }, () => {
        this.retrieveQuote()
      })
    })
    .catch(error => console.log('error'))
  }

  retrieveQuote(){
    if (this.state.quotesDB.length > 0 ){
      let random = Math.floor(Math.random() * this.state.quotesDB.length)
      let randomized = this.state.quotesDB[random]
      this.state.quotesDB.splice(random,1)

      this.setState({
        quote: randomized.quote,
        author: randomized.author
      })} 
    else{
      alert('You have reached the end. Please refresh the page.')
    }
  }


  render (){
    let uri = this.state.quote + " - " + this.state.author;
    let encoded = encodeURIComponent(uri);
    return (
      <div id="quote-box">
        <h1>Quote for Your Thoughts?</h1>
        <p id="text">{this.state.quote}</p>
        <p id="author">{this.state.author}</p>
        <button id="new-quote" onClick={this.retrieveQuote}>New Quote</button>
        <button><a id="tweet-quote" href={`https://twitter.com/intent/tweet?text=` + encoded}>Tweet</a></button>
      </div>
    )
  }
}

export default App;
