import React from 'react';
import  './App.css';

class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      quotesDB:[],
      quote: '',
      author: ''
    }
    this.retrieveQuote = this.retrieveQuote.bind(this)
  }

  componentDidMount(){
    fetch('https://gist.githubusercontent.com/apatrickg/de7a7fae9ab4bd94d7965d01ca253f21/raw/84a56b56c8de22a2a571e80a897189a19ea1b6df/quotes.json')
    .then(response => response.json())
    .then(response => {
      this.setState({
        quotesDB: response.quotes
      }, () => {
        this.retrieveQuote()
      })
    })
    .catch(error => console.log('error'))
  }

  retrieveQuote(){
    let random = this.state.quotesDB.splice(Math.floor(Math.random() * this.state.quotesDB.length),1)
    let randomized = this.state.quotesDB[random]

    this.setState({
      quote: randomized.quote,
      author: randomized.author
    })
  }


  render (){
    return (
      <div id="quote-box">
        <h1>Quote for Your Thoughts?</h1>
        <p id="text">{this.state.quote}</p>
        <p id="author">{this.state.author}</p>
        <button id="new-quote" onClick={this.retrieveQuote}>New Quote</button>
      </div>
    )
  }
}

export default App;
