import React from 'react';
import  './App.css';

class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      author: '',
      quote: '',
    }
  }




  componentDidMount(){
    fetch('http://my-json-server.typicode.com/apatrickg/quotes/db')
      .then(response=> response.json())
      .then(quotes => this.setState({ quote:quotes }) )
  }

  render (){
    return (
      <div id="quote-box">
        <div id="text"></div>
        <div id="author"></div>
        <div id="new-quote"></div>
      </div>
    )
  }
}

export default App;
