import React from 'react';
import  './App.css';

class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      author: '',
      quote: '',
    }
  }

  componentDidMount(){
    fetch('http://my-json-server.typicode.com/apatrickg/quotes/db')
      .then(response => response.json())
      .then(quotes => {
          let randomIndex = Math.floor(Math.random() * quotes.length)
          let randomQuote = quotes[randomIndex][1];
          let randomAuthor = quotes[randomIndex][2];

          this.setState({author:randomAuthor});
          this.setState({quote: randomQuote});
      })
  }

  render (){
    let  { quote, author } = this.state;
    return (
      <div id="quote-box">
        <div id="text">{ quote }</div>
        <div id="author"> { author }</div>
        <div id="new-quote"></div>
      </div>
    )
  }
}

export default App;
