import Quote from './components/Quote'
import  './App.css';

class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      quotesDB:[],
      quote: '',
      author: ''
    }
    this.retrieveQuote = this.retrieveQuote.bind(this)
  }

  componentDidMount(){
    fetch('https://gist.githubusercontent.com/apatrickg/de7a7fae9ab4bd94d7965d01ca253f21/raw/84a56b56c8de22a2a571e80a897189a19ea1b6df/quotes.json')
    .then(response => response.json())
    .then(response => {
      this.setState({
        quotesDB: response.quotes
      }, () => {
        this.retrieveQuote()
      })
    })
    .catch(error => console.log('error'))
  }

