    let randomized = this.state.quotesDB[random]

    // this.setState({
    //   quote: randomized.quote,
    //   author: randomized.author
    // })
  }


  render (){
    return (
      <div id="quote-box">
        <Quote quote={this.state.quote} author={this.state.author}/>
      </div>
    )
  }
}

export default App;
